package com.biz.loop;

public class Loop_12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<10; i++) {
			for(int j=0; j<10; j++) {
				System.out.printf(j + " ");
			}
			System.out.println();
		}
		System.out.println("==========================");
		
		for(int i=0; i<10; i++) {
			for(int j=0; j<10; j++) {
				System.out.printf("* ");
			}
		System.out.println();
		}
	}

}
